sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;
	c.inputData = "";
	c.notesData = $scope.data.data;

	c.checkForInput = function() {
		var placeholderEl = document.querySelector('.placeholder');
		if(c.inputData.length >= 1) {
			placeholderEl.classList.add('filled');
		} else if(c.inputData.length == 0) {
			placeholderEl.classList.remove('filled');
		}
	}
};

sp_widget.css:
.input-group {
  position: relative;
  margin-top: 1rem;
}

.input {
  border-radius: 2px;
  outline: none;
  border: 1px solid rgba(0,0,0,0.5);
  background: none;
}

.placeholder {
  padding: 0 0 0.2em;
  position: absolute;
  left: 0.5em;
  top: 0.15em;
  z-index: -1;
  transition: all 0.2s linear;
}

.filled {
  z-index: 1;
  top: -1.2em;
  left: -0.5em;
  color: red;
}
